    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {                                             
        pet = new Pet();
        
        if (txtNome.getText().equals("") ||
            txtRaca.getText().equals("") ||
            txtIdade.getText().equals("") ||
            txtSexo.getText().equals("")
        ){
            JOptionPane.showMessageDialog(rootPane, "Favor cadastrar os dados corretamente");
        }
        else{
        
        pet.setNome(txtNome.getText());
        pet.setRaca(txtRaca.getText());
        pet.setIdade(Integer.parseInt(txtIdade.getText()));
        pet.setSexo(txtSexo.getText().charAt(0));
      
        listPet.add(pet);
        
        JOptionPane.showMessageDialog(rootPane, "Cadastro efetuado");
            
        txtNome.setText("");
        txtRaca.setText("");
        txtIdade.setText("");
        txtSexo.setText("");
        
        }
        
        //JOptionPane.showMessageDialog(rootPane, aluno.getNome()+ " foi cadastrado com sucesso");
    }                                            

    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {                                           
         if (listPet.isEmpty())
        {JOptionPane.showMessageDialog(rootPane, "Nenhum dado cadastrado");
        }
        else{
            String str = "";
            for (int i = 0; i < listPet.size(); i++){
                str += "\n--------\n";
                str += listPet.get(i).imprimir();
            }
            JOptionPane.showMessageDialog(rootPane, str);
        }
    }                                          

    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {                                             
       for (int i=0; i < listPet.size(); i++) {
            if (listPet.get(i).getNome().equals(txtPesquisarNome.getText())) {
                JOptionPane.showMessageDialog(rootPane, listPet.get(i).imprimir());
               }
                                   
       }
       
        

    }                                            

    private void btnApagarActionPerformed(java.awt.event.ActionEvent evt) {                                          
        for (int i = 0; i < listPet.size(); i++) {
            if (listPet.get(i).getNome().equals(txtPesquisarNome.getText())) {
                listPet.remove(i);
                JOptionPane.showMessageDialog(rootPane, "Deletado");        
            }
        }
    }                                         

